<script setup lang="ts">
import { ref, type PropType } from 'vue'
import { type GenericElection, submit_single_party_vote } from '@/backend'
import type { VoteOption } from '@/utils'

const props = defineProps({
  election: {
    type: Object as PropType<GenericElection>,
    required: true
  },
  options: {
    type: Array as PropType<VoteOption[]>,
    required: true
  },
  vote_token: {
    type: String,
    required: false
  }
})

const emits = defineEmits({
  complete: () => true,
  error: (error: string) => true
})

const loading = ref<boolean>(false)
const approve = ref(false)

async function submit() {
  loading.value = true
  const response = await submit_single_party_vote(
    props.election.id,
    props.vote_token,
    approve.value
  )
  const data = await response.text()
  loading.value = false
  if (!response.ok) {
    emits(`error`, data)
    return
  }

  emits(`complete`)
}
</script>

<template>
  <div>
    <div v-if="loading">
      <p>Loading...</p>
    </div>
    <br />
    <img
      src="data:image/webp;base64,"
    />
    <br />
    <h3>Candidate: {{ options[0].name }}</h3>
    <br />
    <div>
      <input id="yes" type="checkbox" v-model="approve" />
      <label for="yes">Yes</label>
    </div>
    <br />
    <button class="vote-button" @click="submit">üó≥Ô∏èVOTEüó≥Ô∏è</button>
  </div>
</template>

